import React from 'react';
import './App.css';
import Header from './components/Header';
import Footer from './components/Footer';
import Sidebar from './components/Sidebar';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import PreOrdersPage from './components/PreOrdersPage';
import BidsPage from './components/BidsPage';
import MainContent from './components/MainContent';
import ConsumerPage from './components/ConsumerPage'; // Import the ConsumerPage
import ConsumerLandingPage from './components/ConsumerLandingPage'; // Import the ConsumerLandingPage
import LandingPage from './components/LandingPage'; // Import the LandingPage
import SellerRegistration from './components/SellerRegistration'; // Import the new SellerRegistration component


function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

function AppContent() {
  const location = useLocation();

  const isLandingPage = location.pathname === '/';

  return (
    <div className="app-container">
      {!isLandingPage && <Header />}
      <div className={`main-wrapper ${location.pathname === '/consumer' || location.pathname === '/consumer-landing' ? 'no-sidebar' : ''}`}>
        {!isLandingPage && location.pathname !== '/consumer' && location.pathname !== '/consumer-landing' && <Sidebar />}
        <div className={`main-content ${isLandingPage || location.pathname === '/consumer' || location.pathname === '/consumer-landing' ? 'full-width' : ''}`}>
          <Routes>
            <Route exact path="/" element={<LandingPage />} /> {/* Set LandingPage as the default route */}
            <Route path="/seller-dashboard" element={<MainContent />} />
            <Route path="/pre-orders" element={<PreOrdersPage />} />
            <Route path="/bids" element={<BidsPage />} />
            <Route path="/consumer" element={<ConsumerPage />} /> {/* Add the new route */}
            <Route path="/consumer-landing" element={<ConsumerLandingPage />} /> {/* Add the new route */}
          </Routes>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
